    }

    dijkstra(s){
        if(s>=this.state.points.length)
        return
        if(s==="" && s!=='0')
        {
            alert("Enter a source vertex")
            return
        }
        for(let i=0;i<s.length;i++)
        {
            if(s[i]!=='0' && s[i]!=='1' && s[i]!=='2' && s[i]!=='3' && s[i]!=='4' && s[i]!=='5' && s[i]!=='6' && s[i]!=='7' && s[i]!=='8' && s[i]!=='9')
