import React, { Component } from 'react'

export class Canvas extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             points:[]
        }
    }
    
    getMousePosition(event) { 
        let x = event.clientX-430;//this.refs.svg.style.marginLeft ;
        let y = event.clientY-this.refs.svg.style.marginTop ;
        this.setState((prev)=>{
            points:prev.points.push({
                x:x,
                y:y
            })
        })
        this.forceUpdate();
    }
    render() {
        var pts=this.state.points.map((x,idx)=>{
            return(
            <circle cx={x.x} cy={x.y} r="10" stroke="black" stroke-width="1" fill="green" />
            )
        })
        var ptsidx=this.state.points.map((x,idx)=>{
            return(
                <text fill="#000000" font-size="10" style={{backgroundColor:"white"}} font-family="Arial" x={x.x-4} y={x.y+4}>{idx}</text>
            )
        })
        return (
            <div>
            <svg ref='svg' className="svgStyles" width="500" height="400" style={{backgroundColor: 'rgb(215, 201, 248)',Top:'100'}} onClick={(event)=>this.getMousePosition(event)}>
            {pts}
            {ptsidx}
            </svg>
            </div>
        )
    }
}

export default Canvas
