import React, { Component } from 'react'

export class Canvas extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             points:[]
        }
    }
    
    getMousePosition(event) { 
        let x = event.clientX - rect.left;
        let y = event.clientY - rect.top;
        this.setState((prev)=>{
            points:prev.points.push({
                x:x,
                y:y
            })
        })
    }
    render() {
        var pts=this.state.points.map((x)=>{
            return(
            <circle cx={x.x} cy={x.y} r="10" stroke="green" stroke-width="2" fill="blue" />
            )
        })
        return (
            <div style={{marginTop:'6%'}}>
            <svg ref='svg' width="500" height="400" style={{backgroundColor: 'palegreen'}} >
            <g onMouseDown={(event)=>this.getMousePosition(event)}></g>
            </svg>
            </div>
        )
    }
}

export default Canvas
