import React, { Component } from 'react'

export class Canvas extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             points:[]
        }
    }
    
    getMousePosition(event) { 
        let x = event.clientX-this.refs.svg.x ;
        let y = event.clientY-this.refs.svg.y ;
        this.setState((prev)=>{
            points:prev.points.push({
                x:x,
                y:y
            })
        })
        console.log(this.state.points)
    }
    render() {
        var pts=this.state.points.map((x)=>{
            return(
            <circle cx={x.x} cy={x.y} r="10" stroke="black" stroke-width="2" fill="green" />
            )
        })
        return (
            <div>
            <svg ref='svg' width="500" height="400" style={{backgroundColor: 'palegreen'}} onMouseDown={(event)=>this.getMousePosition(event)} >
            <circle cx={20} cy={20} r="10" stroke="black" stroke-width="2" fill="blue" />
            {pts}
            </svg>
            </div>
        )
    }
}

export default Canvas
