import React, { Component } from 'react'

export class Canvas extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             points:[],
             varline:''
        }
    }
    
    getMousePosition(event) { 
        let x = event.clientX;//this.refs.svg.style.marginLeft ;
        let y = event.clientY-this.refs.svg.style.marginTop ;
        this.setState((prev)=>{
            points:prev.points.push({
                x:x,
                y:y
            })
        })
        this.forceUpdate();
    }

    drawLine(e){
        console.log("s");
        let x=`<line x1=${0} y1=${0} x2="200" y2="200" style="stroke:rgb(255,0,0);stroke-width:2" />`
        this.setState({
            varline:x
        })
        this.forceUpdate();
    }
    render() {
        var pts=this.state.points.map((x,idx)=>{
            return(
            <circle key={"point"+idx} cx={x.x} cy={x.y} r="10" stroke="black" onDrag={(event)=>this.drawLine(event)} strokeWidth="1" fill="green" style={{draggable:'true'}} />
            )
        })
        var ptsidx=this.state.points.map((x,idx)=>{
            return(
                <text fill="#fff" key={"index"+idx} fontSize="10" fontFamily="Arial" x={x.x-4} y={x.y+4} onClick={(event)=>this.drawLine(event)}>{idx}</text>
            )
        })
        return (
            <div>
            <svg ref='svg' className="svgStyles" width={window.innerWidth} height="400" style={{backgroundColor: 'rgb(215, 201, 248)'}} onClick={(event)=>this.getMousePosition(event)}>
            {pts}
            {ptsidx}
            {this.state.varline}
            </svg>
            </div>
        )
    }
}

export default Canvas
